const ItemDetail = ({details}) => {
  const [cantidad,setCantidad] = useState(0); 
  const {addToCart}  = useContext(CartContext);


    const onAdd = (cantidad) => {
    setCantidad(cantidad);
    addToCart(details,cantidad); //le paso el detalle y cantida 
  };



